export function createOrder(order) {
  console.log("order: ", order);
  return fetch(process.env.REACT_APP_API_URL + "/order", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(order),
  }).then((data) => data.json());
}

export function listenSSEReceiveBigImageUrl(memo, handleReceiveMessage) {
  console.log("listenSSEReceiveBigImageUrl memo: ", memo);
  const eventSource = new EventSource(
    process.env.REACT_APP_API_URL + "/stream/" + memo
  );

  const handleReceiveSSEMessageLocal = (event) => {
    console.log("handleReceiveSSEMessage: ", event);
    //const eventData = JSON.parse(event.data);
    //setBigImageUrl(event.data);
    handleReceiveMessage(event);
    eventSource.removeEventListener("message", handleReceiveMessage);
    eventSource.close();
  };
  eventSource.removeEventListener("message", handleReceiveMessage);
  eventSource.close();
  eventSource.addEventListener("message", handleReceiveMessageLocal);
  /*
  return () => {
    // Remove event listener and close the connection on unmount
    console.log("before removeEventListener");
    eventSource.removeEventListener("message", handleReceiveMessage);
    eventSource.close();
  };
  */
}
