import React from "react";
import Dialog from "@material-ui/core/Dialog";
import CloseIcon from "@material-ui/icons/Close";
import Fab from "@material-ui/core/Fab";
import GalleryAppBar from "../components/GalleryAppBar";
import Slide from "@material-ui/core/Slide";
import Paper from "@material-ui/core/Paper";

export default function ImageDialog({ handleCloseToParent, bigImageUrl }) {
  const useStyles = makeStyles((theme) => ({
    appBar: {
      position: "relative",
    },
    paper: {
      marginTop: theme.spacing(3),
      marginBottom: theme.spacing(3),
      padding: theme.spacing(2),
      [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
        marginTop: theme.spacing(6),
        marginBottom: theme.spacing(6),
        padding: theme.spacing(3),
      },
    },
  }));
  const handleClose = () => {
    handleCloseToParent();
  };

  const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
  });

  return (
    <Paper className={classes.paper}>
      <Dialog
        fullScreen
        open={true}
        onClose={handleClose}
        TransitionComponent={Transition}
      >
        <GalleryAppBar></GalleryAppBar>
        <img src={bigImageUrl} alt={bigImageUrl} style={{ width: "100%" }} />
        <Fab color="primary" aria-label="Close" onClick={() => handleClose()}>
          <CloseIcon />
        </Fab>
      </Dialog>
    </Paper>
  );
}
