import StellarSdk from "stellar-sdk";

export function createAccount() {
  const pair = StellarSdk.Keypair.random();
  //Fund account. (Hopefully, this only works against the stellar test network :-))
  return fetch(
    `https://friendbot.stellar.org?addr=${encodeURIComponent(
      this.pair.publicKey()
    )}`
  )
    .then(function (data) {
      console.log(data);
      return pair.json(); //only interested in the pair
    })
    .catch(function (error) {
      console.log("Failed!", error);
    });
}

export function getAccountBalance(account) {
  return fetch(
    `https://horizon.stellar.org/accounts/${encodeURIComponent(account)}`
  )
    .then(function (account) {
      console.log(account);
      return { balance: account.balances[0].balance };
    })
    .catch(function (error) {
      console.log("Failed!", error);
    });
}
