import StellarSdk from "stellar-sdk";

export function createAccount() {
  const pair = StellarSdk.Keypair.random();
  /*
  console.log("publickey", pair.publicKey());
  console.log("secret", pair.secret());
  console.log("created pair: ", pair);
  //Fund account. (This only works for the stellar test network)
  return Promise.resolve({
    publickey: pair.publicKey(),
    secret: pair.secret(),
  });
  */

  return fetch(
    `https://friendbot.stellar.org?addr=${encodeURIComponent(
      pair.publicKey()
    )}`,
    { mode: "cors" }
  )
    .then(function (fund_account_response) {
      return {
        publickey: pair.publicKey(),
        secret: pair.secret(),
      };
    })
    .catch(function (error) {
      console.log("Failed!", error);
    });
}

export function getAccountBalance(account) {
  console.log("account: ", account);
  return fetch(
    `https://horizon.stellar.org/accounts/${encodeURIComponent(account)}`,
    { mode: "cors" }
  )
    .then(function (account) {
      console.log(account);
      return { balance: account.balances[0].balance };
    })
    .catch(function (error) {
      console.log("Failed!", error);
    });
}
